package coreconfig_test

import (
	. "code.cloudfoundry.org/cli/cf/configuration/coreconfig"
	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"
)

var _ = Describe("NewTokenInfo", func() {
	It("decodes a string into TokenInfo when there is no padding present", func() {
		accessToken := "bearer eyJhbGciOiJSUzI1NiJ9..kjFJHi0Qir9kfqi2eyhHy6kdewhicAFu8hrPR1a5AxFvxGB45slKEjuP0_72cM_vEYICgZn3PcUUkHU9wghJO9wjZ6kiIKK1h5f2K9g-Iprv9BbTOWUODu1HoLIvg2TtGsINxcRYy_8LW1RtvQc1b4dBPoopaEH4no-BIzp0E5E"
		decodedInfo, err := DecodeAccessToken(accessToken)

		Expect(err).NotTo(HaveOccurred())
		Expect(string(decodedInfo)).To(ContainSubstring("user1@example.com"))
	})

	It("decodes a string into TokenInfo when there is doubling padding present", func() {
		accessToken := "bearer eyJhbGciOiJSUzI1NiJ9..dcgrGjPvTjYvg8dTSZY5ecZZTNt59IYd442VaEXXvLNB_WQCAdbVOxiJ14ogzQkkzDDw60Q2lbw4z6HrqM1a-BNpYfRmvaIP_79GpIZC6OzQy_PgA1whL27pO7_ABkSJT1CEgJQJMTQlYOiZNHvFTWen3G4O6ey680cxIN5VvbFjmmQHCuwANE9_GqnYYvoI9tS1nERku8DX2H9KH5NAgDa52-p0NhLnZRqYjGss6EyPYkwYN5w2OizfYUmEYVWo8K1Q45_TGMoE-LgZe2mGWwv0euLYBoFTkYhtBMj91dQagLrL1aGcmDKPc6ivkXtfpN4Zv7FJ9OXJ2DPQyHKRpw"
		decodedInfo, err := DecodeAccessToken(accessToken)

		Expect(err).NotTo(HaveOccurred())
		Expect(string(decodedInfo)).To(ContainSubstring("tlang@gopivotal.com"))
	})

	It("decodes a string into TokenInfo when there is single padding present", func() {
		accessToken := "bearer eyJhbGciOiJSUzI1NiJ9..dcgrGjPvTjYvg8dTSZY5ecZZTNt59IYd442VaEXXvLNB_WQCAdbVOxiJ14ogzQkkzDDw60Q2lbw4z6HrqM1a-BNpYfRmvaIP_79GpIZC6OzQy_PgA1whL27pO7_ABkSJT1CEgJQJMTQlYOiZNHvFTWen3G4O6ey680cxIN5VvbFjmmQHCuwANE9_GqnYYvoI9tS1nERku8DX2H9KH5NAgDa52-p0NhLnZRqYjGss6EyPYkwYN5w2OizfYUmEYVWo8K1Q45_TGMoE-LgZe2mGWwv0euLYBoFTkYhtBMj91dQagLrL1aGcmDKPc6ivkXtfpN4Zv7FJ9OXJ2DPQyHKRpw"
		decodedInfo, err := DecodeAccessToken(accessToken)

		Expect(err).NotTo(HaveOccurred())
		Expect(string(decodedInfo)).To(ContainSubstring("tlang1@gopivotal.com"))
	})
})
